
import JumpCard from "../game-logic/cards/jumpCard.js";

// saves cards asa pair [className,parameters]
export const allCards = [
	[JumpCard,[5]],
	[JumpCard,[3]]
];

// picks a card from allCards
export function numberToCard(n){
	return pairToCard(allCards[n]);
}

/**
 * goes through every card and compares the name generated by the card to te name saved
 */
export function nameToCard(name) {
	for (let i=0; i < allCards.length; i++){
		let card = numberToCard(i);
		if (card.name===name){
			return card;
		}
		console.log(card.name,name);
	}
}

function pairToCard(pair){
	const [cardClass,parameters] = pair;
	return new cardClass(...parameters);
}
